exports.getCompletedReferrals = async (req, res) => {
  try {
    const referrals = await Referral.find({
      pasien: req.user.id,
      status: "selesai",
    }).sort({ tanggal: -1 });

    res.json(referrals);
  } catch (err) {
    console.error(err.message);
    res.status(500).send("Server Error");
  }
};

exports.getAllReferrals = async (req, res) => {
  try {
    const referrals = await Referral.find()
      .populate("pasien", "nama")
      .sort({ tanggal: -1 });

    res.json(referrals);
  } catch (err) {
    console.error(err.message);
    res.status(500).send("Server Error");
  }
};
